$(".color-changer-icon").hover(function () {
        let currentSrc = $(this).attr("src");
        let altSrc = $(this).attr("alt-img-src");
        let imgId = $(this).attr("id");
        $("img#"+imgId).attr("src",altSrc);
        $("img#"+imgId).attr("alt-img-src",currentSrc); 
    }, function () {
        let currentSrc = $(this).attr("src");
        let altSrc = $(this).attr("alt-img-src");
        let imgId = $(this).attr("id");
        $("img#"+imgId).attr("src",altSrc);
        $("img#"+imgId).attr("alt-img-src",currentSrc); 
    }
);

// Sidenav Toggle
$("button#sidenav-toggle-button").click(function (e) { 
  e.preventDefault();
  $("div#sidenav-menu").removeClass("d-none");
  $("div#sidenav-overlay").removeClass("d-none");
});
$("div#sidenav-overlay").click(function (e) { 
  e.preventDefault();
  $("div#sidenav-menu").addClass("d-none");
  $("div#sidenav-overlay").addClass("d-none");
});

// sEction nav
$("a.section-nav-link").click(function() {
  let targetSectionId = $(this).attr("section-id");
  $([document.documentElement, document.body]).animate({
      scrollTop: $("section#"+targetSectionId).offset().top
  }, 1000);
});



$(function () {
  $('[data-toggle="tooltip"]').tooltip()
})

$(".emi-calc-variable").change(function (e) { 
  e.preventDefault();
  let principle = $("input#loanAmount").val();
  let actualPrinciple = principle*100000;
  let months = $("input#loanMonths").val();
  let roi = $("input#loanROI").val();
  $("span#pincipleValue").html(principle+" Lakhs");
  $("span#monthValue").html(months);
  $("span#roiValue").html(roi);
  let simpleInterest = (actualPrinciple*months*roi)/100;
  let amountPayable = actualPrinciple+simpleInterest;
  let emiAmount = amountPayable/months;
  $("span#caclulated-emi").html(parseInt(emiAmount));
  $("span#caclulated-amount").html(parseInt(amountPayable));
});