// Sidenav Toggle
$("button#sidenav-toggle-button").click(function (e) { 
  e.preventDefault();
  $("div#sidenav-menu").removeClass("d-none");
  $("div#sidenav-overlay").removeClass("d-none");
});
$("div#sidenav-overlay").click(function (e) { 
  e.preventDefault();
  $("div#sidenav-menu").addClass("d-none");
  $("div#sidenav-overlay").addClass("d-none");
});

// sEction nav
$("a.section-nav-link").click(function() {
  let targetSectionId = $(this).attr("section-id");
  $([document.documentElement, document.body]).animate({
      scrollTop: $("section#"+targetSectionId).offset().top
  }, 1000);
});


let prevScrollpos = window.pageYOffset;
window.onscroll = function() {
let currentScrollPos = window.pageYOffset;
  if (currentScrollPos<400) {
    document.getElementById("desktop-section-nav").style.display = "none";

  } else {
      
    document.getElementById("desktop-section-nav").style.display = "block";

  }
}

